mapper.select:
SELECT EXISTS(
    SELECT 1 FROM scheduler_job_servers WHERE system_name = #{systemName}
)

SELECT FROM scheduler_job_servers
AND ( LOWER(system_name) LIKE LOWER(CONCAT('%', #{textSearch}, '%')) OR LOWER(host_name) LIKE LOWER(CONCAT('%', #{textSearch}, '%')) OR LOWER(host_ip_addr) LIKE LOWER(CONCAT('%', #{textSearch}, '%')) )
ORDER BY system_name ASC LIMIT #{limit} OFFSET #{offset}

mapper.insert:
INSERT INTO scheduler_job_servers (
    system_id, system_name, host_name, host_ip_addr, secondary_host_ip_addr,
    system_comments, queue_name, folder_path, secondary_folder_path, ssh_user,
    agent_status, frst_reg_date, last_chg_date, frst_reg_user_id, last_reg_user_id
) VALUES (
    #{systemId}, #{systemName}, #{hostName}, #{hostIpAddr}, #{secondaryHostIpAddr},
    #{systemComments}, #{queueName}, #{folderPath}, #{secondaryFolderPath}, #{sshUser},
    #{agentStatus}, #{frstRegDate}, #{lastChgDate}, #{frstRegUserId}, #{lastRegUserId}
)

mapper.update:
UPDATE scheduler_job_servers
SET agent_status = #{agentStatus}
WHERE system_id = #{systemId}
