mapper.select:
SELECT EXISTS(
    SELECT 1 FROM scheduler_job_servers WHERE system_name = #{systemName}
)

SELECT s.system_id, s.system_name, s.host_name, s.host_ip_addr, s.secondary_host_ip_addr, s.system_comments, s.queue_name, s.folder_path, s.secondary_folder_path, s.ssh_user, s.agent_status, s.is_healthy, s.agent_port, s.frst_reg_date, s.last_chg_date, s.frst_reg_user_id, s.last_reg_user_id, u1.user_name as creator, u2.user_name as last_modifier FROM scheduler_job_servers s LEFT JOIN scheduler_users u1 ON s.frst_reg_user_id = u1.id LEFT JOIN scheduler_users u2 ON s.last_reg_user_id = u2.id
AND ( LOWER(s.system_name) LIKE LOWER(CONCAT('%', #{textSearch}, '%')) OR LOWER(s.host_name) LIKE LOWER(CONCAT('%', #{textSearch}, '%')) OR LOWER(s.host_ip_addr) LIKE LOWER(CONCAT('%', #{textSearch}, '%')) )
ORDER BY s.system_name ASC LIMIT #{limit} OFFSET #{offset}

mapper.insert:
INSERT INTO scheduler_job_servers (
    system_id, system_name, host_name, host_ip_addr, secondary_host_ip_addr,
    system_comments, queue_name, folder_path, secondary_folder_path, ssh_user,
    agent_status, is_healthy, agent_port, frst_reg_date, last_chg_date, frst_reg_user_id, last_reg_user_id
) VALUES (
    #{systemId}, #{systemName}, #{hostName}, #{hostIpAddr}, #{secondaryHostIpAddr},
    #{systemComments}, #{queueName}, #{folderPath}, #{secondaryFolderPath}, #{sshUser},
    #{agentStatus}, #{isHealthy}, #{agentPort}, #{frstRegDate}, #{lastChgDate}, #{frstRegUserId}, #{lastRegUserId}
)

mapper.update:
UPDATE scheduler_job_servers
SET agent_status = #{agentStatus}
WHERE system_id = #{systemId}

UPDATE scheduler_job_servers
SET is_healthy = #{isHealthy}
WHERE system_id = #{systemId}
